{
  "bugs": [
      {
         "id": "1",
         "description": "The class Dfp does not handle the case where the exponent is MIN_EXP - digits when the number is less than the smallest number it can represent. It should flush to zero at this point.",
         "severity": "major"
      },
      {
         "id": "2",
         "description": "The class Dfp does not handle the case where the exponent is MAX_EXP + digits when the number is greater than the largest number it can represent. It should flush to infinity at this point.",
         "severity": "major"
      },
      {
         "id": "3",
         "description": "The class Dfp does not handle the case where the mantissa is zero and the exponent is not zero. It should be able to represent zero with any exponent.",
         "severity": "minor"
      },
      {
         "id": "4",
         "description": "The class Dfp does not handle the case where the mantissa is not zero and the exponent is zero. It should be able to represent any non-zero number with exponent zero.",
         "severity": "minor"
      },
      {
         "id": "5",
         "description": "The class Dfp does not handle the case where the mantissa is NaN and the exponent is not zero. It should be able to represent NaN with any exponent.",
         "severity": "minor"
      },
      {
         "id": "6",
         "description": "The class Dfp does not handle the case where the mantissa is not NaN and the exponent is NaN. It should be able to represent NaN with any mantissa.",
         "severity": "minor"
      },
      {
         "id": "7",
         "description": "The class Dfp does not handle the case where the mantissa and exponent are both NaN. It should be able to represent NaN with NaN mantissa and exponent.",
         "severity": "minor"
      }
   ]
}
