{
  "bugs": [
      {
         "type": "Potential NullPointerException",
         "description": "The constraints parameter in the SimplexTableau constructor is not checked for null before being passed to the normalizeConstraints method. If constraints is null, a NullPointerException will be thrown.",
         "location": "SimplexTableau constructor"
      },
      {
         "type": "Potential IndexOutOfBoundsException",
         "description": "The getConstraintTypeCounts method is not checked for empty constraint collection before being called. If the constraint collection is empty, an IndexOutOfBoundsException will be thrown.",
         "location": "getConstraintTypeCounts method"
      },
      {
         "type": "Potential ArithmeticException",
         "description": "The epsilon parameter in the SimplexTableau constructor is not checked for negative value. If epsilon is negative, an ArithmeticException will be thrown.",
         "location": "SimplexTableau constructor"
      },
      {
         "type": "Potential IllegalArgumentException",
         "description": "The maxUlps parameter in the SimplexTableau constructor is not checked for non-positive value. If maxUlps is non-positive, an IllegalArgumentException will be thrown.",
         "location": "SimplexTableau constructor"
      }
   ]
}
